def do_baz():    messages = json.dumps({"main":"Condition failed on page baz"})    session['messages'] = messages    return redirect(url_for('.do_foo', messages=messages))@app.route('/foo')def do_foo():    messages = request.args['messages']  # counterpart for url_for()    messages = session['messages']       # counterpart for session    return render_template("foo.html", messages=json.loads(messages))